import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Register() {

  const [quote, setQuote] = useState(null);

  useEffect(() => {
    const category = 'happiness';
    const apiKey = 'hSEE/gkqi4fRRNSRATry4g==YW87MZmtcAZTisYf';

    axios.get(`https://api.api-ninjas.com/v1/quotes?category=${category}`, {
      headers: {
        'X-Api-Key': apiKey
      }
    })
    .then((response) => {
      setQuote(response.data[0]);
    })
    .catch((error) => {
      console.error('Error:', error.response ? error.response.data : error.message);
    });
  }, []);

  const [values, setValues] = useState({
    userName: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(values); 
  };

  const handleChange = (e) => {
    setValues({...values, [e.target.name]: e.target.value});
  };

  return (
    <div className='flex'>
      <div>
        <form onSubmit={(e) => handleSubmit(e)}>
          <input type="text" name='userName' placeholder='Username' onChange={(e) => handleChange(e)} />
          <input type="email" name='email' placeholder='Email' onChange={(e) => handleChange(e)} />
          <input type="password" name='password' placeholder='Password' onChange={(e) => handleChange(e)} />
          <input type="password" name='confirmPassword' placeholder='Confirm Password' onChange={(e) => handleChange(e)} />
          <button type='submit'>Submit</button>
        </form>
      </div>

      <div>
        {quote ? (
          <div>
            <p>{quote.quote}</p>
            <p><strong>- {quote.author}</strong></p>
          </div>
        ) : (
          <p>Loading quote...</p>
        )}
      </div>
    </div>
  );
}
