import axios from 'axios';
import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { allUsers, host } from '../utils/APIRoutes';
import Contacts from '../components/Contacts';
import Welcome from '../components/Welcome';
import ChatContainer from '../components/ChatContainer';
import { emit } from 'process';

export default function Chat() {
    const socket = useRef();
    const navigate = useNavigate();
    const [contacts, setContacts] = useState([]);
    const [currentUser, setCurrentUser] = useState(undefined);
    const [currentChat, setCurrentChat] = useState(undefined);

    useEffect(() => {
        const getCurrentUser = async () => {
            const storedUser = localStorage.getItem('chat-app-user');
            if (!storedUser) {
                navigate('/login');
            } else {
                setCurrentUser(JSON.parse(storedUser));
            }
        };
        getCurrentUser();
    }, [navigate]);

    useEffect(() => {
        if(currentUser){
            socket.current = io(host);
            socket.current = emit("add-user", currentUser._id);
        }
    },[currentUser])

    useEffect(() => {
        const fetchContacts = async () => {
            if (currentUser) {
                if (currentUser.isAvatarImageSet) {
                    const data = await axios.get(`${allUsers}/${currentUser._id}`);
                    setContacts(data.data);
                } else {
                    navigate("/setAvatar");
                }
            }
        };
        fetchContacts();
    }, [currentUser, navigate]);

    return (
        <div className="flex h-screen">
            <div className="w-1/4 bg-[#1e132c] flex flex-col justify-between">
                {currentUser && (
                    <Contacts 
                        contacts={contacts} 
                        currentUser={currentUser} 
                        setCurrentChat={setCurrentChat} // Pass the function here
                    />
                )}
            </div>

            <div className="w-3/4 bg-[#261833] ">
                {currentChat === undefined ? (
                    <Welcome currentUser={currentUser} />
                ) : (
                    <ChatContainer currentUser={currentUser} currentChat={currentChat} socket={socket} />
                )}
            </div>
        </div>
    );
}
