import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { allUsers } from '../utils/APIRoutes';
import Contacts from '../components/Contacts';

import robot from '../robot.gif'

export default function Chat() {
    const navigate = useNavigate();
    const [contacts, setContacts] = useState([]);
    const [currentUser, setCurrentUser] = useState(undefined);

    useEffect(() => {
        const getCurrentUser = async () => {
            const storedUser = localStorage.getItem('chat-app-user');
            if (!storedUser) {
                navigate('/login');
            } else {
                setCurrentUser(JSON.parse(storedUser));
            }
        };
        getCurrentUser();
    }, [navigate]);

    useEffect(() => {
        const fetchContacts = async () => {
            if (currentUser) {
                if (currentUser.isAvatarImageSet) {
                    const data = await axios.get(`${allUsers}/${currentUser._id}`);
                    setContacts(data.data);
                } else {
                    navigate("/setAvatar");
                }
            }
        };
        fetchContacts();
    }, [currentUser, navigate]);

    return (
        <div className="flex h-screen">
            {/* Sidebar */}
            <div className="w-1/4 bg-[#1e132c] flex flex-col justify-between">
                {currentUser && <Contacts contacts={contacts} currentUser={currentUser} />}
            </div>

            {/* Main Chat Area */}
            <div className="w-3/4 bg-[#261833] flex items-center justify-center">
                <div className="text-center">
                    <img
                        src={robot} // Replace with the actual image path or URL for the robot.
                        alt="Robot"
                        className="mx-auto mb-8 w-40 h-40"
                    />
                    <h1 className="text-4xl font-bold text-white">
                        Welcome, <span className="text-[#9b6bfb]">{currentUser?.username}!</span>
                    </h1>
                    <p className="text-[#fbcbfb] mt-2">Please select a chat to Start messaging.</p>
                </div>
            </div>
        </div>
    );
}
