import React from 'react';

export default function Messages({ messages, currUser }) {
  return (
    <div className="flex-grow overflow-y-auto p-4">
      <div className="flex flex-col space-y-4">
        {messages.map((message, index) => (
          <div key={index} className={`flex ${message.fromSelf ? 'justify-end' : 'justify-start'}`}>
            <div 
              className={`text-white p-3 rounded-lg max-w-xs ${message.fromSelf === currUser._id ? 'bg-[#fb6cd4]' : 'bg-[#9b6bfb]'}`}
            >
              <p>{message.message}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
