import React, { useEffect, useState } from 'react';

export default function Contacts({ contacts, currentUser }) {
    const [currentUserName, setCurrentUserName] = useState(undefined);
    const [currentUserImage, setCurrentUserImage] = useState(undefined);
    const [currentSelected, setCurrentSelected] = useState(undefined);

    useEffect(() => {
        if (currentUser) {
            setCurrentUserName(currentUser.username);
            setCurrentUserImage(currentUser.avatarImage);
        }
    }, [currentUser]);

    const changeCurrentChat = (index, contacts) => {};

    return (
        currentUserName && currentUserImage && (
            <div className='flex flex-col space-y-4'>
                {/* Logo Section */}
                <div className='text-center text-[#fbcbfb] text-2xl font-bold mb-6'>
                    Chat App
                </div>

                {/* Contacts List */}
                <div className='space-y-4'>
                    {Array.isArray(contacts) && contacts.length > 0 ? (
                        contacts.map((contact, index) => {
                            return (
                                <div
                                    className={`flex items-center space-x-4 p-4 rounded-lg ${
                                        index === currentSelected ? 'bg-[#6441a0]' : 'bg-[#533655]'
                                    }`}
                                    key={index}
                                    onClick={() => setCurrentSelected(index)}
                                >
                                    <img
                                        src={`data:image/svg+xml;base64,${contact.avatarImage}`}
                                        alt="avatar"
                                        className="w-10 h-10 rounded-full"
                                    />
                                    <div className='text-white'>
                                        <h3>{contact.username}</h3>
                                    </div>
                                </div>
                            );
                        })
                    ) : (
                        <p className='text-[#fbcbfb]'>No contacts available.</p>
                    )}
                </div>

                {/* Current User Info */}
                <div className='flex items-center space-x-4 mt-6 p-4 bg-[#52347c] rounded-lg'>
                    <img
                        src={`data:image/svg+xml;base64,${currentUser.avatarImage}`}
                        alt="avatar"
                        className="w-12 h-12 rounded-full"
                    />
                    <div className='text-white'>
                        <h3>{currentUser.username}</h3>
                    </div>
                </div>
            </div>
        )
    );
}
