import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { allUsers } from '../utils/APIRoutes';
import Contacts from '../components/Contacts';

export default function Chat() {
    const navigate = useNavigate();
    const [contacts, setContacts] = useState([]);
    const [currentUser, setCurrentUser] = useState(undefined);

    useEffect(() => {
        const getCurrentUser = async () => {
            const storedUser = localStorage.getItem('chat-app-user');
            if (!storedUser) {
                navigate('/login');
            } else {
                setCurrentUser(JSON.parse(storedUser));
            }
        };
        getCurrentUser();
    }, [navigate]);

    useEffect(() => {
        const fetchContacts = async () => {
            if (currentUser) {
                if (currentUser.isAvatarImageSet) {
                    const data = await axios.get(`${allUsers}/${currentUser._id}`);
                    setContacts(data.data);
                } else {
                    navigate("/setAvatar");
                }
            }
        };
        fetchContacts();
    }, [currentUser, navigate]);

    return (
        <div className='flex justify-center items-center min-h-screen bg-[#261833]'>
            <div className='bg-[#533655] w-full max-w-md p-6 shadow-lg rounded-lg'>
                {currentUser && <Contacts contacts={contacts} currentUser={currentUser} />}
            </div>
        </div>
    );
}
