import React, { useEffect, useState } from 'react'
import robot from '../robot.gif'

export default function Welcome({currentUser}) {
    const [currentUserName, setCurrentUserName] = useState(undefined);
    useEffect(() => {
        setCurrentUserName(currentUser.username)
    })

  return (
    <div>
        <div className="text-center">
        <img
            src={robot} // Replace with the actual image path or URL for the robot.
            alt="Robot"
            className="mx-auto mb-8 w-40 h-40"
        />
        <h1 className="text-4xl font-bold text-white">
            Welcome, <span className="text-[#9b6bfb]">{currentUserName}!</span>
        </h1>
        <p className="text-[#fbcbfb] mt-2">Please select a chat to Start messaging.</p>
        </div>
    </div>
  )
}
