import React, { useEffect, useState } from 'react';
import Logout from './Logout'


export default function Contacts({ contacts, currentUser, setCurrentChat }) { 
    const [currentUserName, setCurrentUserName] = useState(undefined);
    const [currentUserImage, setCurrentUserImage] = useState(undefined);
    const [currentSelected, setCurrentSelected] = useState(undefined);

    useEffect(() => {
        if (currentUser) {
            setCurrentUserName(currentUser.username);
            setCurrentUserImage(currentUser.avatarImage);
        }
    }, [currentUser]);

    const changeCurrentChat = (index, contact) => {
        setCurrentSelected(index);
        setCurrentChat(contact); // Set the current chat with the selected contact
    };

    return (
        <div className="flex flex-col h-full">
            <div className="p-4">
                <h2 className="text-white text-2xl font-bold">SNAPPY</h2>
            </div>

            <div className="flex-grow overflow-y-auto">
                {Array.isArray(contacts) && contacts.length > 0 ? (
                    contacts.map((contact, index) => {
                        return (
                            <div
                                className={`flex items-center space-x-4 p-4 cursor-pointer ${
                                    index === currentSelected ? 'bg-[#6441a0]' : ''
                                }`}
                                key={index}
                                onClick={() => changeCurrentChat(index, contact)} 
                            >
                                <img
                                    src={contact.avatarImage ? `data:image/svg+xml;base64,${contact.avatarImage}` : '/path/to/default/avatar.png'} // Fallback to a default avatar if undefined
                                    alt="avatar"
                                    className="w-10 h-10 rounded-full"
                                />
                                <div className="text-white">
                                    <h3>{contact.username || 'Unknown User'}</h3> {/* Fallback username */}
                                </div>
                            </div>
                        );
                    })
                ) : (
                    <p className="text-[#fbcbfb] p-4">No contacts available.</p>
                )}
            </div>

            <div className="bg-[#533655]  flex items-center space-x-4">
                <img
                    src={currentUserImage ? `data:image/svg+xml;base64,${currentUserImage}` : '/path/to/default/avatar.png'} // Fallback for current user
                    alt="avatar"
                    className="w-12 h-12 rounded-full"
                />
                <div className="text-white">
                    <h3>{currentUserName || 'Current User'}</h3>
                </div>
                <div className="ml-auto"> {/* Pushes Logout to the end */}
                    <Logout />
                </div>
            </div>

        </div>
    );
}
