import React, { useEffect, useState } from 'react';

export default function Contacts({ contacts, currentUser, setCurrentChat }) { // Accept setCurrentChat as prop
    const [currentUserName, setCurrentUserName] = useState(undefined);
    const [currentUserImage, setCurrentUserImage] = useState(undefined);
    const [currentSelected, setCurrentSelected] = useState(undefined);

    useEffect(() => {
        if (currentUser) {
            setCurrentUserName(currentUser.username);
            setCurrentUserImage(currentUser.avatarImage);
        }
    }, [currentUser]);

    const changeCurrentChat = (index, contact) => {
        setCurrentSelected(index);
        setCurrentChat(contact);
    };

    console.log(currentUser)

    return (
        <div className="flex flex-col h-full">
            <div className="p-4">
                <h2 className="text-white text-2xl font-bold">SNAPPY</h2>
            </div>

            <div className="flex-grow overflow-y-auto">
                {Array.isArray(contacts) && contacts.length > 0 ? (
                    contacts.map((contact, index) => {
                        return (
                            <div
                                className={`flex items-center space-x-4 p-4 cursor-pointer ${
                                    index === currentSelected ? 'bg-[#6441a0]' : ''
                                }`}
                                key={index}
                                onClick={() => changeCurrentChat(index, contact)} // Call the function with contact
                            >
                                <img
                                    src={`data:image/svg+xml;base64,${contact.avatarImage}`}
                                    alt="avatar"
                                    className="w-10 h-10 rounded-full"
                                />
                                <div className="text-white">
                                    <h3>{contact.username}</h3>
                                </div>
                            </div>
                        );
                    })
                ) : (
                    <p className="text-[#fbcbfb] p-4">No contacts available.</p>
                )}
            </div>

            <div className="bg-[#533655] p-4 flex items-center space-x-4">
                <img
                    src={`data:image/svg+xml;base64,${currentUserImage}`}
                    alt="avatar"
                    className="w-12 h-12 rounded-full"
                />
                <div className="text-white">
                    <h3>{currentUserName}</h3>
                </div>
            </div>
        </div>
    );
}
