import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { allUsers } from '../utils/APIRoutes';
import Contacts from '../components/Contacts';

export default function Chat() {
    const navigate = useNavigate();
    const [contacts, setContacts] = useState([]);
    const [currentUser, setCurrentUser] = useState(undefined)
    useEffect(async () => {
        if (!localStorage.getItem('chat-app-user')) {
          navigate('/login');
        }else{
            setCurrentUser(await JSON.parse(localStorage.getItem('chat-app-user')));
        }
      }, []);

      useEffect(async () => {
        if(currentUser){
            if(currentUser.isAvatarImageSet){
                const data = await axios.get(`${allUsers}/${currentUser._id}`);
                setContacts(data.data);
            }else{
                navigate("/setAvatar");
            }
        }
      }, [currentUser])
  return (
    <div className='flex justify-center items-center min-h-screen bg-[#2e1d45]'>
        <div className='bg-[#1e132c] w-full max-w-md p-6 shadow-lg rounded-lg'>
            <Contacts contacts={contacts} currentUser={currentUser}/>
        </div>
    </div>
  )
}
