import React, { useState } from 'react';
import EmojiPicker from 'emoji-picker-react';
import { IoMdSend } from 'react-icons/io';
import { MdEmojiEmotions } from 'react-icons/md';

export default function ChatInput() {
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [message, setMessage] = useState(''); // State to store the message input

  const toggleEmojiPicker = () => {
    setShowEmojiPicker(!showEmojiPicker);
  };

  const onEmojiClick = (event, emojiObject) => {
    setMessage(message + emojiObject.emoji); // Append the selected emoji to the message
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the message submission logic here
    console.log('Message sent:', message);
    setMessage(''); // Clear the input field after sending
  };

  return (
    <div className="p-4 border-t border-[#9b6bfb] bg-[#2e1d45]">
      <div className="flex items-center space-x-4">
        {/* Emoji Picker Icon */}
        <div className="relative">
          <MdEmojiEmotions
            className="text-2xl text-[#fb6cd4] cursor-pointer hover:text-[#9b6bfb] transition duration-300"
            onClick={toggleEmojiPicker}
          />
          {showEmojiPicker && (
            <div className="absolute bottom-12">
              <EmojiPicker onEmojiClick={onEmojiClick} theme="dark" />
            </div>
          )}
        </div>

        {/* Input Field */}
        <form className="flex-grow flex items-center space-x-4" onSubmit={handleSubmit}>
          <input
            type="text"
            value={message} // Bind the input value to message state
            onChange={(e) => setMessage(e.target.value)} // Update message when typing
            placeholder="Type your message..."
            className="w-full p-3 rounded-lg bg-[#4a374c] text-white border border-[#9b6bfb] focus:outline-none focus:border-[#fb6cd4]"
          />
          <button
            type="submit"
            className="p-2 rounded-full bg-[#9b6bfb] text-white hover:bg-[#fb6cd4] transition duration-300"
          >
            <IoMdSend className="text-xl" />
          </button>
        </form>
      </div>
    </div>
  );
}
