import React, { useEffect, useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import { loginRoute } from '../utils/APIRoutes';
import { Link, useNavigate } from "react-router-dom";

export default function Login() {
  const navigate = useNavigate();
  const [values, setValues] = useState({
    username: "",
    password: "",
  });

  const toastOptions = {
    position: "bottom-right",
    autoClose: 5000,
    pauseOnHover: true,
    theme: "dark",
  };

  useEffect(() => {
    if(localStorage.getItem('chat-app-user')){
      navigate('/');
    }
  }, [])

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (handleValidation()) {
      const { username, password } = values;
      try {
        const { data } = await axios.post(loginRoute, {
          username, 
          password
        });

        if (data.status === false) {
          toast.error(data.msg, toastOptions);
        }
        if (data.status === true) {
          localStorage.setItem('chat-app-user', JSON.stringify(data.user));
          navigate("/");
        }
      } catch (error) {
        toast.error("Registration failed. Please try again.", toastOptions);
        console.error("Error:", error);
      }
    }
  };

  const handleValidation = () => {
    const { username, password } = values;

    if (username === "" || password === "") {
      toast.error("User Name and Password Required", toastOptions);
      return false;
    }

    if (password.length < 8) {
      toast.error("Password must be at least 8 characters long.", toastOptions);
      return false;
    }

    return true;
  };

  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  return (
    <>
      <div className="flex justify-center items-center min-h-screen bg-gray-100">
        <div className="w-full max-w-md p-6 bg-white shadow-lg rounded-lg">
          <div><p className="text-3xl font-bold text-center pb-5">Register</p></div>
          <form onSubmit={handleSubmit} className="space-y-4">
            <input 
              type="text" 
              name="username"
              placeholder="Username" 
              onChange={handleChange} 
              className="w-full p-2 border border-gray-300 rounded-md"
            />
            <input 
              type="password" 
              name="password" 
              placeholder="Password" 
              onChange={handleChange} 
              className="w-full p-2 border border-gray-300 rounded-md"
            />
            <button 
              type="submit" 
              className="w-full bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600"
            >
              Login
            </button>
            <p className='text-center'>Don't have an account? <Link to={"/register"}> <a className='font-bold'>Signup</a> </Link></p>
          </form>
        </div>
      </div>
      <ToastContainer />
    </>
  );
}
