import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Register() {

  const [quote, setQuote] = useState(null);

  useEffect(() => {
    const category = 'happiness';
    const apiKey = 'hSEE/gkqi4fRRNSRATry4g==YW87MZmtcAZTisYf';

    axios.get(`https://api.api-ninjas.com/v1/quotes?category=${category}`, {
      headers: {
        'X-Api-Key': apiKey
      }
    })
    .then((response) => {
      setQuote(response.data[0]);
    })
    .catch((error) => {
      console.error('Error:', error.response ? error.response.data : error.message);
    });
  }, []);

  const [values, setValues] = useState({
    userName: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(values); 
  };

  const handleChange = (e) => {
    setValues({...values, [e.target.name]: e.target.value});
  };

  return (
    <div className='min-h-screen justify-center items-start font-sans p-3'>
      <div>
        {quote ? (
          <div>
            <p className='text-3xl text-center'>{quote.quote}</p>
            <p className='text-center'><strong>- {quote.author}</strong></p>
          </div>
        ) : (
          <p>Loading quote...</p>
        )}
      </div>
      <div>
        <form onSubmit={(e) => handleSubmit(e)} className="space-y-4">
          <input 
            type="text" 
            name="userName" 
            placeholder="Username" 
            onChange={(e) => handleChange(e)} 
            className="w-full p-2 border border-gray-300 rounded-md"
          />
          <input 
            type="email" 
            name="email" 
            placeholder="Email" 
            onChange={(e) => handleChange(e)} 
            className="w-full p-2 border border-gray-300 rounded-md"
          />
          <input 
            type="password" 
            name="password" 
            placeholder="Password" 
            onChange={(e) => handleChange(e)} 
            className="w-full p-2 border border-gray-300 rounded-md"
          />
          <input 
            type="password" 
            name="confirmPassword" 
            placeholder="Confirm Password" 
            onChange={(e) => handleChange(e)} 
            className="w-full p-2 border border-gray-300 rounded-md"
          />
          <button 
            type="submit" 
            className="w-full bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600"
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
