import React, { useEffect, useState } from 'react';
import robot from '../robot.gif';

export default function Welcome({ currentUser }) {
    const [currentUserName, setCurrentUserName] = useState('');

    useEffect(() => {
        if (currentUser) { // Check if currentUser is defined
            setCurrentUserName(currentUser.username);
        }
    }, [currentUser]); // Add currentUser as a dependency

    return (
        <div className="text-center flex items-center justify-center">
            <img
                src={robot} 
                alt="Robot"
                className="mx-auto mb-8 w-40 h-40"
            />
            <h1 className="text-4xl font-bold text-white">
                Welcome, <span className="text-[#9b6bfb]">{currentUserName || 'User'}!</span> {/* Fallback to 'User' */}
            </h1>
            <p className="text-[#fbcbfb] mt-2">Please select a chat to start messaging.</p>
        </div>
    );
}
